
public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int num1=5;
         int num2=10;
         int add=num1 + num2;
         int sub=num1-num2;
         int div=num1 /num2;
         int mul=num1 *num2;
         int mod=num1%num2;
         System.out.println(add);
         System.out.println(sub);
         System.out.println(div);
         System.out.println(mul);
         System.out.println(mod);
         
        
         

	}
}
