
public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int a=10;
           int b=20;
           boolean c=false;
           System.out.println(+a);
           System.out.println(+b);
           System.out.println(-a);
           System.out.println(-b);
         //  System.out.println(!a);   // it giving error because ! operator is used for checking or in relational operators like boolean true or false
           System.out.println(!c); 
	}

}
