
public class WhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int a =5;
   
   
   while(a<10) {
	   System.out.println(a);
	   a++;
   }
   
  int b=20;
   do {
	   System.out.println("B");
	   b++;
   }
   while(b<10);
	
   }
	}


